// Cross-language verification test for Go encryption implementation.
//
// This program loads test vectors generated by the JavaScript implementation
// and verifies that Go can correctly decrypt them.
//
// Usage: go run verify_go.go

package main

import (
	"encoding/binary"
	"encoding/hex"
	"encoding/json"
	"fmt"
	"os"
	"path/filepath"
	"regexp"
	"strings"
)

// TestVector represents a single test case
type TestVector struct {
	Name         string                 `json:"name"`
	Schema       string                 `json:"schema"`
	RootType     string                 `json:"root_type"`
	OriginalJSON map[string]interface{} `json:"original_json"`
	OriginalHex  string                 `json:"original_hex"`
	EncryptedHex string                 `json:"encrypted_hex"`
}

// TestVectors is the complete test data structure
type TestVectors struct {
	KeyHex       string       `json:"key_hex"`
	FlatcVersion string       `json:"flatc_version"`
	Vectors      []TestVector `json:"vectors"`
}

func main() {
	// Get the directory of this script
	execPath, _ := os.Executable()
	baseDir := filepath.Dir(execPath)

	// Try to find test_vectors.json
	vectorsPath := filepath.Join(baseDir, "test_vectors.json")
	if _, err := os.Stat(vectorsPath); os.IsNotExist(err) {
		// Try current directory
		vectorsPath = "test_vectors.json"
		if _, err := os.Stat(vectorsPath); os.IsNotExist(err) {
			// Try relative to current working directory
			cwd, _ := os.Getwd()
			vectorsPath = filepath.Join(cwd, "wasm/examples/cross-language-test/test_vectors.json")
		}
	}

	data, err := os.ReadFile(vectorsPath)
	if err != nil {
		fmt.Printf("Error reading test vectors: %v\n", err)
		fmt.Println("Run this from the cross-language-test directory")
		os.Exit(1)
	}

	var testData TestVectors
	if err := json.Unmarshal(data, &testData); err != nil {
		fmt.Printf("Error parsing test vectors: %v\n", err)
		os.Exit(1)
	}

	key, err := hex.DecodeString(testData.KeyHex)
	if err != nil {
		fmt.Printf("Error decoding key: %v\n", err)
		os.Exit(1)
	}

	fmt.Printf("Testing against %s\n", testData.FlatcVersion)
	fmt.Printf("Key: %s...\n\n", testData.KeyHex[:16])

	passed := 0
	failed := 0

	for _, vector := range testData.Vectors {
		// Skip if no encryption happened
		if vector.OriginalHex == vector.EncryptedHex {
			fmt.Printf("SKIP: %s (no encrypted fields present)\n", vector.Name)
			continue
		}

		encrypted, err := hex.DecodeString(vector.EncryptedHex)
		if err != nil {
			fmt.Printf("FAIL: %s (hex decode error: %v)\n", vector.Name, err)
			failed++
			continue
		}

		// Decrypt using our Go implementation
		decrypted := make([]byte, len(encrypted))
		copy(decrypted, encrypted)
		DecryptBuffer(decrypted, vector.Schema, key, vector.RootType)

		decryptedHex := hex.EncodeToString(decrypted)

		if decryptedHex == vector.OriginalHex {
			fmt.Printf("PASS: %s\n", vector.Name)
			passed++
		} else {
			fmt.Printf("FAIL: %s\n", vector.Name)
			if len(vector.OriginalHex) > 80 {
				fmt.Printf("  Expected: %s...\n", vector.OriginalHex[:80])
				fmt.Printf("  Got:      %s...\n", decryptedHex[:80])
			} else {
				fmt.Printf("  Expected: %s\n", vector.OriginalHex)
				fmt.Printf("  Got:      %s\n", decryptedHex)
			}
			failed++
		}
	}

	fmt.Printf("\nResults: %d passed, %d failed\n", passed, failed)
	if failed > 0 {
		os.Exit(1)
	}
}

