var rcon = [11]byte{0x00, 0x01, 0x02, 0x04, 0x08, 0x10, 0x20, 0x40, 0x80, 0x1b, 0x36}

func gfMul(a, b byte) byte {
	var p byte
	for i := 0; i < 8; i++ {
		if b&1 != 0 {
			p ^= a
		}
		hiBit := a & 0x80
		a <<= 1
		if hiBit != 0 {
			a ^= 0x1b
		}
		b >>= 1
	}
	return p
}

func aes256KeyExpansion(key []byte) []byte {
	roundKeys := make([]byte, 240)
	copy(roundKeys[:32], key)

	for i := 8; i < 60; i++ {
		temp := make([]byte, 4)
		copy(temp, roundKeys[(i-1)*4:i*4])

		if i%8 == 0 {
			t := temp[0]
			temp[0] = sbox[temp[1]] ^ rcon[i/8]
			temp[1] = sbox[temp[2]]
			temp[2] = sbox[temp[3]]
			temp[3] = sbox[t]
		} else if i%8 == 4 {
			for j := 0; j < 4; j++ {
				temp[j] = sbox[temp[j]]
			}
		}

		for j := 0; j < 4; j++ {
			roundKeys[i*4+j] = roundKeys[(i-8)*4+j] ^ temp[j]
		}
	}

	return roundKeys
}

func subBytes(state []byte) {
	for i := 0; i < 16; i++ {
		state[i] = sbox[state[i]]
	}
}

func shiftRows(state []byte) {
	temp := state[1]
	state[1] = state[5]
	state[5] = state[9]
	state[9] = state[13]
	state[13] = temp

	temp = state[2]
	state[2] = state[10]
	state[10] = temp
	temp = state[6]
	state[6] = state[14]
	state[14] = temp

	temp = state[15]
	state[15] = state[11]
	state[11] = state[7]
	state[7] = state[3]
	state[3] = temp
}

func mixColumns(state []byte) {
	for i := 0; i < 4; i++ {
		a := []byte{state[i*4], state[i*4+1], state[i*4+2], state[i*4+3]}
		state[i*4+0] = gfMul(a[0], 2) ^ gfMul(a[1], 3) ^ a[2] ^ a[3]
		state[i*4+1] = a[0] ^ gfMul(a[1], 2) ^ gfMul(a[2], 3) ^ a[3]
		state[i*4+2] = a[0] ^ a[1] ^ gfMul(a[2], 2) ^ gfMul(a[3], 3)
		state[i*4+3] = gfMul(a[0], 3) ^ a[1] ^ a[2] ^ gfMul(a[3], 2)
	}
}

func addRoundKey(state, roundKey []byte) {
	for i := 0; i < 16; i++ {
		state[i] ^= roundKey[i]
	}
}

func aesEncryptBlock(key, input []byte) []byte {
	roundKeys := aes256KeyExpansion(key)
	state := make([]byte, 16)
	copy(state, input)

	addRoundKey(state, roundKeys[:16])

	for round := 1; round < 14; round++ {
		subBytes(state)
		shiftRows(state)
		mixColumns(state)
		addRoundKey(state, roundKeys[round*16:(round+1)*16])
	}

	subBytes(state)
	shiftRows(state)
	addRoundKey(state, roundKeys[14*16:15*16])

	return state
}

func aesCtrKeystream(key, nonce []byte, length int) []byte {
	keystream := make([]byte, length)
	counter := make([]byte, 16)
	copy(counter, nonce)

	offset := 0
	for offset < length {
		block := aesEncryptBlock(key, counter)
		toCopy := 16
		if length-offset < 16 {
			toCopy = length - offset
		}
		copy(keystream[offset:], block[:toCopy])
		offset += toCopy

		for i := 15; i >= 0; i-- {
			counter[i]++
			if counter[i] != 0 {
				break
			}
		}
	}

	return keystream
}

func deriveKey(masterKey, info []byte, outLength int) []byte {
	out := make([]byte, outLength)

	for i := 0; i < len(masterKey) && i < outLength; i++ {
		out[i] = masterKey[i]
	}

	var hashVal byte
	for _, b := range info {
		hashVal ^= b
		hashVal = (hashVal << 1) | (hashVal >> 7)
	}

	for i := 0; i < outLength; i++ {
		out[i] ^= hashVal
		hashVal = byte((int(hashVal)*31 + i) & 0xFF)
	}

	if outLength >= 16 {
		temp := aesEncryptBlock(masterKey, out[:16])
		copy(out[:minInt(outLength, 16)], temp[:minInt(outLength, 16)])
		if outLength > 16 {
			temp2 := aesEncryptBlock(masterKey, temp)
			copy(out[16:minInt(outLength, 32)], temp2[:minInt(outLength-16, 16)])
		}
	}

	return out
}

func minInt(a, b int) int {
	if a < b {
		return a
	}
	return b
}

// EncryptionContext holds the encryption key
type EncryptionContext struct {
	key []byte
}

// NewEncryptionContext creates a new context
func NewEncryptionContext(key []byte) *EncryptionContext {
	return &EncryptionContext{key: key}
}

func (ctx *EncryptionContext) deriveFieldKey(fieldID int) []byte {
	info := make([]byte, 19)
	copy(info, []byte("flatbuffers-field"))
	info[17] = byte((fieldID >> 8) & 0xFF)
	info[18] = byte(fieldID & 0xFF)
	return deriveKey(ctx.key, info, 32)
}

func (ctx *EncryptionContext) deriveFieldIV(fieldID int) []byte {
	info := make([]byte, 16)
	copy(info, []byte("flatbuffers-iv"))
	info[14] = byte((fieldID >> 8) & 0xFF)
	info[15] = byte(fieldID & 0xFF)
	return deriveKey(ctx.key, info, 16)
}

// FieldInfo represents a field from the schema
type FieldInfo struct {
	Name        string
	ID          int
	Type        string
	Encrypted   bool
	ElementType string
	ElementSize int
}

func getTypeSize(typeName string) int {
	sizes := map[string]int{
		"bool": 1, "byte": 1, "ubyte": 1,
		"short": 2, "ushort": 2,
		"int": 4, "uint": 4, "float": 4,
		"long": 8, "ulong": 8, "double": 8,
	}
	if size, ok := sizes[typeName]; ok {
		return size
	}
	return 0
}

func getBaseType(typeName string) string {
	scalarTypes := map[string]bool{
		"bool": true, "byte": true, "ubyte": true,
		"short": true, "ushort": true,
		"int": true, "uint": true,
		"long": true, "ulong": true,
		"float": true, "double": true,
	}
	if scalarTypes[typeName] {
		return typeName
	}
	if typeName == "string" {
		return "string"
	}
	return "struct"
}

func parseSchemaForEncryption(schemaContent, rootType string) []FieldInfo {
	var fields []FieldInfo

	pattern := regexp.MustCompile(`(?s)table\s+` + regexp.QuoteMeta(rootType) + `\s*\{([^}]+)\}`)
	match := pattern.FindStringSubmatch(schemaContent)
	if match == nil {
		return fields
	}

	tableBody := match[1]
	fieldPattern := regexp.MustCompile(`(\w+)\s*:\s*(\[?\w+\]?)\s*(?:\(([^)]*)\))?`)

	fieldID := 0
	for _, m := range fieldPattern.FindAllStringSubmatch(tableBody, -1) {
		name := m[1]
		fieldType := m[2]
		attributes := ""
		if len(m) > 3 {
			attributes = m[3]
		}

		isEncrypted := strings.Contains(attributes, "encrypted")
		isVector := strings.HasPrefix(fieldType, "[") && strings.HasSuffix(fieldType, "]")
		baseType := fieldType
		if isVector {
			baseType = fieldType[1 : len(fieldType)-1]
		}

		field := FieldInfo{
			Name:      name,
			ID:        fieldID,
			Encrypted: isEncrypted,
		}

		if isVector {
			field.Type = "vector"
			field.ElementType = getBaseType(baseType)
			field.ElementSize = getTypeSize(baseType)
		} else {
			field.Type = getBaseType(baseType)
		}

		fields = append(fields, field)
		fieldID++
	}

	return fields
}

func readUint32(buffer []byte, offset int) uint32 {
	return binary.LittleEndian.Uint32(buffer[offset:])
}

func readInt32(buffer []byte, offset int) int32 {
	return int32(binary.LittleEndian.Uint32(buffer[offset:]))
}

func readUint16(buffer []byte, offset int) uint16 {
	return binary.LittleEndian.Uint16(buffer[offset:])
}

func encryptRegion(buffer []byte, start, length int, key, iv []byte) {
	keystream := aesCtrKeystream(key, iv, length)
	for i := 0; i < length; i++ {
		buffer[start+i] ^= keystream[i]
	}
}

func processTable(buffer []byte, tableOffset int, fields []FieldInfo, ctx *EncryptionContext) {
	vtableOffsetDelta := readInt32(buffer, tableOffset)
	vtableOffset := tableOffset - int(vtableOffsetDelta)

	vtableSize := readUint16(buffer, vtableOffset)

	for _, field := range fields {
		fieldVtableIdx := (field.ID + 2) * 2

		if fieldVtableIdx >= int(vtableSize) {
			continue
		}

		fieldOffset := readUint16(buffer, vtableOffset+fieldVtableIdx)
		if fieldOffset == 0 {
			continue
		}

		fieldLoc := tableOffset + int(fieldOffset)

		if !field.Encrypted {
			continue
		}

		key := ctx.deriveFieldKey(field.ID)
		iv := ctx.deriveFieldIV(field.ID)

		switch field.Type {
		case "bool", "byte", "ubyte":
			encryptRegion(buffer, fieldLoc, 1, key, iv)
		case "short", "ushort":
			encryptRegion(buffer, fieldLoc, 2, key, iv)
		case "int", "uint", "float":
			encryptRegion(buffer, fieldLoc, 4, key, iv)
		case "long", "ulong", "double":
			encryptRegion(buffer, fieldLoc, 8, key, iv)
		case "string":
			stringOffset := readUint32(buffer, fieldLoc)
			stringLoc := fieldLoc + int(stringOffset)
			stringLen := readUint32(buffer, stringLoc)
			stringData := stringLoc + 4
			if stringData+int(stringLen) <= len(buffer) {
				encryptRegion(buffer, stringData, int(stringLen), key, iv)
			}
		case "vector":
			vecOffset := readUint32(buffer, fieldLoc)
			vecLoc := fieldLoc + int(vecOffset)
			vecLen := readUint32(buffer, vecLoc)
			vecData := vecLoc + 4
			elemSize := field.ElementSize
			if elemSize == 0 {
				elemSize = 1
			}
			totalSize := int(vecLen) * elemSize
			if vecData+totalSize <= len(buffer) {
				encryptRegion(buffer, vecData, totalSize, key, iv)
			}
		}
	}
}

// EncryptBuffer encrypts a FlatBuffer in place
func EncryptBuffer(buffer []byte, schemaContent string, key []byte, rootType string) {
	ctx := NewEncryptionContext(key)
	fields := parseSchemaForEncryption(schemaContent, rootType)
	rootOffset := readUint32(buffer, 0)
	processTable(buffer, int(rootOffset), fields, ctx)
}

// DecryptBuffer decrypts a FlatBuffer in place (same as encrypt for CTR mode)
func DecryptBuffer(buffer []byte, schemaContent string, key []byte, rootType string) {
	EncryptBuffer(buffer, schemaContent, key, rootType)
}
